mapper.select:
select * from (select rowNum rNum,board_no,board_name,board_title, board_hit,board_ref,board_step,board_level,board_date, board_type from (select * from board
board_title like #{find_name}
board_cont like #{find_name}
order by board_ref desc, board_level asc)) where rNum >= #{startrow} and rNum <= #{endrow} and board_type= 'A'

select * from (select rowNum rNum,board_no,board_name,board_title, board_hit,board_ref,board_step,board_level,board_date, board_type from (select * from board
board_title like #{find_name}
board_cont like #{find_name}
order by board_ref desc, board_level asc)) where rNum >= #{startrow} and rNum <= #{endrow} and board_type= 'B'

select * from (select rowNum rNum,board_no,board_name,board_title, board_hit,board_ref,board_step,board_level,board_date, board_type from (select * from board
board_title like #{find_name}
board_cont like #{find_name}
order by board_ref desc, board_level asc)) where rNum >= #{startrow} and rNum <= #{endrow} and board_type= 'C'

select count(board_no) from (select * from board where board_type='A')




 board_title like #{find_name} 


 board_cont like #{find_name}

select count(board_no) from (select * from board where board_type='B')




 board_title like #{find_name}


 board_cont like #{find_name}

select count(board_no) from (select * from board where board_type='C')




 board_title like #{find_name}


 board_cont like #{find_name}

mapper.update:
update board set board_level=board_level+1
where board_ref=#{board_ref} 
and board_level>#{board_level}

update board set board_name=#{board_name},
board_title=#{board_title},board_cont=#{board_cont}
where board_no=#{board_no}
